function [ out ] = expertWeight2(r1L,r1U,r2L,r2U,r3L,r3U,r4L,r4U,x)
%确定群体决策各个属性的一致水平，然后根据各专家与一致水平的偏差最小化来计算专家权重
%X(1,4)(2,5)(3,6)分别对应第1,2,3个属性，7、8、9、10分别是第1、2、3、4个专家的权重

out=(getdis(r1L(1,1),r1U(1,1),x(1),x(4))+getdis(r1L(2,1),r1U(2,1),x(1),x(4))+getdis(r1L(3,1),r1U(3,1),x(1),x(4))+getdis(r1L(5,1),r1U(5,1),x(1),x(4))+...
    getdis(r1L(1,2),r1U(1,2),x(2),x(5))+getdis(r1L(2,2),r1U(2,2),x(2),x(5))+getdis(r1L(3,2),r1U(3,2),x(2),x(5))+getdis(r1L(5,2),r1U(5,2),x(2),x(5))+...
    getdis(r1L(1,3),r1U(1,3),x(3),x(6))+getdis(r1L(2,3),r1U(2,3),x(3),x(6))+getdis(r1L(3,3),r1U(3,3),x(3),x(6))+getdis(r1L(5,3),r1U(5,3),x(3),x(6)))*x(7)/4+...
    (getdis(r2L(1,1),r2U(1,1),x(1),x(4))+getdis(r2L(2,1),r2U(2,1),x(1),x(4))+getdis(r2L(3,1),r2U(3,1),x(1),x(4))+getdis(r2L(4,1),r2U(4,1),x(1),x(4))+getdis(r2L(5,1),r2U(5,1),x(1),x(4))+getdis(r2L(7,1),r2U(7,1),x(1),x(4))+...
    getdis(r2L(1,2),r2U(1,2),x(2),x(5))+getdis(r2L(2,2),r2U(2,2),x(2),x(5))+getdis(r2L(3,2),r2U(3,2),x(2),x(5))+getdis(r2L(4,2),r2U(4,2),x(2),x(5))+getdis(r2L(5,2),r2U(5,2),x(2),x(5))+getdis(r2L(7,2),r2U(7,2),x(2),x(5))+...
    getdis(r2L(1,3),r2U(1,3),x(3),x(6))+getdis(r2L(2,3),r2U(2,3),x(3),x(6))+getdis(r2L(3,3),r2U(3,3),x(3),x(6))+getdis(r2L(4,3),r2U(4,3),x(3),x(6))+getdis(r2L(5,3),r2U(5,3),x(3),x(6))+getdis(r2L(7,3),r2U(7,3),x(3),x(6)))*x(8)/6+...
    (getdis(r3L(1,1),r3U(1,1),x(1),x(4))+getdis(r3L(3,1),r3U(3,1),x(1),x(4))+getdis(r3L(4,1),r3U(4,1),x(1),x(4))+getdis(r3L(6,1),r3U(6,1),x(1),x(4))+getdis(r3L(7,1),r3U(7,1),x(1),x(4))+...
    getdis(r3L(1,2),r3U(1,2),x(2),x(5))+getdis(r3L(3,2),r3U(3,2),x(2),x(5))+getdis(r3L(4,2),r3U(4,2),x(2),x(5))+getdis(r3L(6,2),r3U(6,2),x(2),x(5))+getdis(r3L(7,2),r3U(7,2),x(2),x(5))+...
    getdis(r3L(1,3),r3U(1,3),x(3),x(6))+getdis(r3L(3,3),r3U(3,3),x(3),x(6))+getdis(r3L(4,3),r3U(4,3),x(3),x(6))+getdis(r3L(6,3),r3U(6,3),x(3),x(6))+getdis(r3L(7,3),r3U(7,3),x(3),x(6)))*x(9)/5+...
    (getdis(r4L(4,1),r4U(4,1),x(1),x(4))+getdis(r4L(5,1),r4U(5,1),x(1),x(4))+getdis(r4L(6,1),r4U(6,1),x(1),x(4))+getdis(r4L(7,1),r4U(7,1),x(1),x(4))+...
    getdis(r4L(4,2),r4U(4,2),x(2),x(5))+getdis(r4L(5,2),r4U(5,2),x(2),x(5))+getdis(r4L(6,2),r4U(6,2),x(2),x(5))+getdis(r4L(7,2),r4U(7,2),x(2),x(5))+...
    getdis(r4L(4,3),r4U(4,3),x(3),x(6))+getdis(r4L(5,3),r4U(5,3),x(3),x(6))+getdis(r4L(6,3),r4U(6,3),x(3),x(6))+getdis(r4L(7,3),r4U(7,3),x(3),x(6)))*x(10)/4;
end

